mapper.select:
select pro.product_no as product_no, pro.product_category as product_category, pro.product_name as product_name, image.upload_path as upload_path, pro.product_price as product_price, (select xs+s+m+l+xl+free as product_stock_total from product_stock where product_no = pro.product_no), pro.product_used as product_used from product as pro , product_image as image where pro.product_no = image.product_no and image.main_image = 1
and pro.product_name ILIKE '%'||#{searchKeyword}||'%'
and pro.product_category = #{category}
and pro.product_used = 1
order by pro.product_no desc offset #{start} limit #{cntPerPage}

mapper.update:
update product_image 
set 
upload_path = #{upload_path},
image_name = #{image_name}
where product_no = #{product_no}
and main_image = 1
